#!/usr/bin/env node


"use strict";

var log = require('engram/es5/logging/logger');

var exitHandler = function exitHandler(shouldExit, err) {

	if (err) {

		log.global.fatal({ err: err }, "uncaught global exception.");
	}

	if (shouldExit) {
		process.exit();
	}
};

module.exports = exitHandler;
