#!/usr/bin/env node


"use strict";

exports.req = function (req) {
	return {
		method: req.method,
		url: req.url,
		headers: req.headers,
		remoteAddress: req.connection.remoteAddress,
		remotePort: req.connection.remotePort,
		reqId: req.id
	};
};

exports.res = function (res) {
	return {
		statusCode: res.statusCode,
		method: res.method,
		headers: res.headers
	};
};

exports.err = function (err) {
	return {
		message: err.message,
		stack: err.stack
	};
};
