#!/usr/bin/env node
"use strict";

var bunyan = require("bunyan");

var log = bunyan.createLogger({
	name: "engram",
	streams: [{
		type: "rotating-file",
		path: "log/engram.log",
		period: "1d",
		count: 5
	}, {
		stream: process.stdout
	}]
});

var stats = bunyan.createLogger({
	name: "stats",
	streams: [{
		type: "rotating-file",
		path: "log/stats.log",
		period: "1d",
		count: 5
	}, {
		stream: process.stdout
	}]
});

var jobResult = bunyan.createLogger({
	name: "job-result",
	streams: [{
		type: "rotating-file",
		path: "log/jobResult.log",
		period: "1d",
		count: 5
	}, {
		stream: process.stdout
	}]
});

module.exports = {
	global: log,
	stats: stats,
	jobResult: jobResult
};