#!/usr/bin/env node


"use strict";

var fs = require('fs');
var path = require('path');
var minifier = require('minifier');

var loadStatic = function loadStatic(fpath, extension, callbacks, fnames) {
	return fnames.map(function (fname) {

		var content = fs.readFileSync(path.join(fpath, fname + extension));

		var processed = callbacks.reduce(function (currentContent, callback) {
			return callback(currentContent);
		}, content);

		return {
			content: processed
		};
	});
};

var compressJS = function compressJS(string) {
	return string;
};

module.exports = {
	loadJS: loadStatic.bind({}, 'public/javascript/es5', '.js', [compressJS]),
	loadCSS: loadStatic.bind({}, 'public/css', '.css', [])
};
