#!/usr/bin/env node


"use strict";

var fs = require('fs');
var path = require('path');

var loadStatic = function loadStatic(fpath, extension, fnames) {
	return fnames.map(function (fname) {

		return {
			content: fs.readFileSync(path.join(fpath, fname + extension))
		};
	});
};

module.exports = {
	loadJS: loadStatic.bind({}, 'public/javascript/es5', '.js'),
	loadCSS: loadStatic.bind({}, 'public/css', '.css')
};
