
"use strict"




var commons = {
	contentType: { },
	random:      { },
	async:       { }
}






commons.random.digits = len => {

	var out = ''

	for (var ith = 0; ith < len; ++ith) {
		out += Math.f
	}

	return out

}

commons.contentType.isHTML = type => {
	return type === 'text/html'
}

commons.contentType.isPDF  = type => {
	return type === 'application/pdf'
}





commons.async.collectMap = (data, fn0, fn1) => {

	var results = [ ]

	data.forEach(datum => {

		fn0(datum, result => {
			results.push(result)
		})

	})


	// this is crappy; link the callbacks with an aggregation step.
	if (results.length === data.length) {

		fn1(results)

	} else {

	}


}





module.exports = commons
