#!/usr/bin/env node

"use strict"





var log          = require('engram/logger')

var Result       = require('engram/result').Result
var Ok           = require('engram/result').Ok
var Err          = require('engram/result').Err

var requestURL   = require('engram/request-url')
var URL          = require('url')

var database     = require('engram/database')
var extractTitle = require('engram/extract-title')
var constants    = require('engram/constants')

var saveTitle    = require('engram/save-title')








var findUntitledURL = callback => {

	var sql = `
	SELECT * FROM url

	WHERE url.title is NULL

	ORDER BY RANDOM()
	LIMIT 1;
	`

	database.do((db, callback) => {

		db.all(sql, (err, rows) => {
			err ? callback(Err(err)) : callback(Ok(rows))
		})

	}, callback)

}




var job = reporter => {
	findUntitledURL(urlResult => {

		urlResult.onErr(err => {
			reporter(Err(err))
		})

		urlResult.onOk(saveTitle.bind({ }, reporter))

	})
}




var reporter = saveResult => {

	constants.BUS.fire(':title', saveResult)

}





module.exports = {
	job,
	reporter,
	interval: 2 * 1000
}
