#!/usr/bin/env node

"use strict"





var log      = require('engram/es5/logger')
var database = require('engram/es5/database')





var exitHandler = (shouldExit, err) => {

	if (err) {

		log.global.fatal({
			message: err.message,
			stack:   err.stack
		}, `uncaught exception`)

	}

	if (shouldExit) {
		process.exit( )
	}

}




module.exports = exitHandler
