#!/usr/bin/env node

"use strict"





exports.req = req => {
	return {
		method:        req.method,
		url:           req.url,
		headers:       req.headers,
        remoteAddress: req.connection.remoteAddress,
        remotePort:    req.connection.remotePort
	}
}

exports.res = res => {
	return {
		statusCode: res.statusCode,
		method:     res.method,
		headers:    res.headers
	}
}

exports.err = err => {
	return {
		message: err.message,
		stack:   err.stack
	}
}
