#!/usr/bin/env node

"use strict"





var fs       = require('fs')
var path     = require('path')
var minifier = require('minifier')





var loadStatic = (fpath, extension, callbacks, fnames) => {
	return fnames.map(fname => {

		var content   = fs.readFileSync(path.join(fpath, fname + extension))

		var processed = callbacks.reduce(
			(currentContent, callback) => callback(currentContent),
			content)

		return {
			content: processed
		}

	})
}





var compressJS = string => {
	return string
}





module.exports = {
	loadJS:  loadStatic.bind({ }, 'public/javascript/es5', '.js', [
		compressJS
	]),
	loadCSS: loadStatic.bind({ }, 'public/css', '.css', [


	])
}
