#!/usr/bin/env node

"use strict"





var log           = require('engram/logger')

var Result        = require('engram/result').Result
var Ok            = require('engram/result').Ok
var Err           = require('engram/result').Err

var extractTitle  = require('engram/extract-title')
var log           = require('engram/logger')
var database      = require('engram/database')

var request       = require('request')
var fs            = require('fs')
var path          = require('path')





var loadStatic = (fpath, extension, fnames) => {
	return fnames.map(fname => {

		return {
			content: fs.readFileSync(path.join(fpath, fname + extension))
		}

	})
}



module.exports = {
	loadJS:  loadStatic.bind({ }, 'public/javascript/es5', '.js'),
	loadCSS: loadStatic.bind({ }, 'public/css',            '.css')
}
