#!/usr/bin/env node

"use strict"





var pages         = require('engram/es6/pages')
var publicFiles   = require('engram/es6/public-files')





{
	var dependencies = { }

	dependencies['javascript-head']   = publicFiles.loadJS([ ])
	dependencies['javascript-bottom'] = publicFiles.loadJS([

		'dependency-is',
		'dependency-mithril',
		'bundle'

	])

	dependencies.css = publicFiles.loadCSS([
		'style'
	])


}





module.exports = ( ) => {
	return pages.index(dependencies)
}
