#!/usr/bin/env node

"use strict"





var is            = require('is')
var log           = require('engram/es5/logger')

var Result        = require('engram/es5/result').Result
var Ok            = require('engram/es5/result').Ok
var Err           = require('engram/es5/result').Err





var startJobs = jobs => {

	var pids = Object.keys(jobs)
		.map(jobname => jobs[jobname])
		.map( ({job, reporter, interval}) => {

			return setInterval(job.bind({ }, reporter), interval)

		})

}







module.exports = startJobs
