#!/usr/bin/env node

"use strict"





var constants = require('engram/es5/constants')
var utils     = require('engram/es5/utils')





var tagRequest = function (req, res, next) {

	if (!req.id) {
		req.id = utils.randomDigits(constants.REQUEST_ID_SIZE)
	}

	if (next) {
		next( )
	}

}




module.exports = tagRequest
