#!/usr/bin/env node
"use strict";

var log = require("engram/logger");
var database = require("engram/database");

var exitHandler = function (shouldExit, err) {

	if (err) {

		log.global.fatal({
			message: err.message,
			stack: err.stack
		}, "uncaught exception");
	}

	if (shouldExit) {
		process.exit();
	}
};

module.exports = exitHandler;