
"use strict"


var serialisers = { }





serialisers.req = req => {
	return {
		method:        req.method,
		url:           req.url,
		headers:       req.headers,
		remoteAddress: req.connection.remoteAddress,
		remotePort:    req.connection.remotePort,
		reqID:         req.id
	}
}

serialisers.res = res => {
	return {
		statusCode: res.statusCode,
		method:     res.method,
		headers:    res.headers	
	}
}

serialisers.err = err => {
	return {
		message: err.message,
		stack:   err.stack.toString( )	
	}
}





module.exports = serialisers

