
"use strict"




var express   = require('express')
var constants = require('engram/commons/constants')
var specs     = require('engram/rest/route-specs')
var implement = require('engram/rest/implement')





var impls = [
	( ) => {

		var main    = [express.static('public')]
		var senders = [ ]

		return {
			id:  constants.routes.ids.GET_PUBLIC_FILES,
  			main,
  			senders
		}

	}
]





var addRoutes = (specs, onPatch) => {

	return impls.reduce((specs, patch) => {
		return implement(specs, patch( ), onPatch)
	}, specs)

}





module.exports = addRoutes
