
"use strict"





var helmet        = require('helmet')
var commons       = require('engram/commons/commons')
var validators    = require('engram/rest/validators')
var composeRoutes = require('engram/rest/compose-routes')
var preprocessors = require('engram/rest/preprocessors')





var handlers = {
	https: {
		get:    { },
		post:   { },
		delete: { },
		all:    { }
	}
}





handlers.https.all['*'] = [
	helmet( )
]




handlers.https.get['/bookmarks'] = [
	(req, res) => {
		res.end( )
	}
]

handlers.https.post['/import']  = [
	(req, res) => {
		res.end( )
	}
]

handlers.https.get['/bookmarks/:id']  = [
	(req, res) => {
		res.end( )
	}
]

handlers.https.delete['/bookmarks/:id']  = [
	(req, res) => {
		res.end( )
	}
]

handlers.https.get['/archive/:id']  = [
	(req, res) => {
		res.end( )
	}
]





module.exports = composeRoutes(validators, composeRoutes(preprocessors, handlers))
