
"use strict"





var log = require('engram/logging/logger')





var preprocessors = {
	https: {
		get:    { },
		post:   { },
		delete: { },
		all:    { }
	}
}





var boilerplate = (req, res) => {

	log.global.info({ }, req.method.toUpperCase( ) + ' ' + req.path)

	req.engram = {
		startTime: process.hrtime( )
	}

}





preprocessors.https.all['*'] = [
	(req, res, next) => {

		boilerplate(req, res)
		next( )

	}
]




preprocessors.https.get['/bookmarks'] = [
	(req, res, next) => {

		boilerplate(req, res)
		next( )

	}
]

preprocessors.https.post['/import']  = [
	(req, res, next) => {

		boilerplate(req, res)
		next( )

	}
]

preprocessors.https.get['/bookmarks/:id']  = [
	(req, res, next) => {

		boilerplate(req, res)
		next( )

	}
]

preprocessors.https.delete['/bookmarks/:id']  = [
	(req, res, next) => {

		boilerplate(req, res)
		next( )

	}
]

preprocessors.https.get['/archive/:id']  = [
	(req, res, next) => {

		boilerplate(req, res)
		next( )

	}
]





module.exports = preprocessors
