
"use strict"



var httpStatus = require('engram/commons/http-status')
var Result     = require('engram/commons/result').Result





var redirectToHTTPS = (req, res, callback) => {

	var redirectResult = Result.of(( ) => {

		res.status(httpStatus.movedPermanently.code)
		res.location(req.originalURL.replace(/^http/, 'https'))
		res.end( )

	})

	callback(redirectResult)

}




module.exports = redirectToHTTPS
