
"use strict"




var is    = require('is')
var parts = require('engram/rest/parts')




var specs = {
	routers: { },
	routes:  [ ]
}





specs.routers = {
	api:  parts.router({

		mount: '/api'

	}),
	app:  parts.router({

		mount: '/'

	}),
	save: parts.router({

		mount:       '/',
		strict:      true,
		mergeParams: true

	})
}




specs.routes = [

	parts.route({
		method: 'all',
		route: '*'
	}),

	parts.route({
		method: 'get',
		route: '/bookmarks'
	}),

	parts.route({
		method: 'post',
		route: '/import'
	}),

	parts.route({
		method: 'get',
		route: '/bookmarks/:id'
	}),

	parts.route({
		method: 'delete',
		route: '/archive/:id'
	})

]






module.exports = specs
