
"use strict"






var express  = require('express')
var handlers = require('engram/rest/handlers')




var routes = {

	http: [
		{
			route:  '*',
			method: 'all',
			fns:    handlers.http.all['*']
		}
	],

	https: [
		{
			route:  '/bookmarks',
			method: 'get',
			fns:    handlers.https.get['/bookmarks']
		},

		{
			route:  '/import',
			method: 'post',
			fns:    handlers.https.post['/import']
		},

		{
			route:  '/bookmarks/:id',
			method: 'get',
			fns:    handlers.https.get['/bookmarks/:id']
		},
		{
			route:  '/bookmarks/:id',
			method: 'delete',
			fns:    handlers.https.delete['/bookmarks/:id']
		},

		{
			route:  '/archive/:id',
			method: 'get',
			fns:    handlers.https.get['/archive/:id']
		}
	]


}










module.exports = routes
