
"use strict"





var senders = {
	https: {
		get:    { },
		post:   { },
		delete: { },
		all:    { }
	}
}




var logElapsed = res => {

	var elapsed      = process.hrtime(res.engram.startTime)
	var nanoseconds  = (elapsed[0] * 1e9) + elapsed[1]
	var milliseconds = Math.round(nanoseconds / 1e6, 0)

}




var logStats = res => {

	logElapsed(res)

}





senders.https.all['*'] = [
	(req, res) => {

		logStats(res)
		res.send( )

	}
]




senders.https.get['/bookmarks'] = [
	(req, res) => {

		logStats(res)
		res.send( )

	}
]

senders.https.post['/import']  = [
	(req, res) => {

		logStats(res)
		res.send( )

	}
]

senders.https.get['/bookmarks/:id']  = [
	(req, res) => {

		logStats(res)
		res.send( )

	}
]

senders.https.delete['/bookmarks/:id']  = [
	(req, res) => {

		logStats(res)
		res.send( )

	}
]

senders.https.get['/archive/:id']  = [
	(req, res) => {

		logStats(res)
		res.send( )

	}
]





module.exports = senders
