
"use strict"




var spec = {
	routers: { },
	routes:  [ ]
}

var parts = {

	router: ({

		mergeParams   = false,
		caseSensitive = true,
		strict        = false,
		mount

	}) => {
		return {mergeParams, caseSensitive, strict, mount}
	},

	route: ({

		router   = 'app',
		method,
		protocol = 'https',
		route

	}) => {
		return {}
	}

}





spec.routers = {
	save: parts.router({

		mount:       '/',
		strict:      true,
		mergeParams: true

	}),
	api:  parts.router({

		mount: '/api'

	}),
	app:  parts.router({

		mount: '/'

	})
}




spec.routes = [

	parts.route({
		method: 'all',
		route: '*'
	}),

	parts.route({
		method: 'get',
		route: '/bookmarks'
	}),

	parts.route({
		method: 'post',
		route: '/import'
	}),

	parts.route({
		method: 'get',
		route: '/bookmarks/:id'
	}),

	parts.route({
		method: 'delete',
		route: '/archive/:id'
	})

]






module.exports = spec
