
"use strict"




var spec = {
	routers: { },
	routes:  { }
}

var parts = {

	router: ({mergeParams = false, caseSensitive = true, strict = false, mount}) => {
		return {mergeParams, caseSensitive, strict, mount}
	},

	route: ({router = 'save'}) => {
		return {}
	}

}





spec.routers = {
	save: parts.router({

		mount:       '/',
		strict:      true,
		mergeParams: true

	}),
	api:  parts.router({

		mount: '/api'

	}),
	app:  parts.router({

		mount: '/'

	})
}






module.exports = spec
