#!/usr/bin/env node
"use strict";

var log = require("engram/logger");

var Result = require("engram/result").Result;
var Ok = require("engram/result").Ok;
var Err = require("engram/result").Err;

var URL = require("url");

var database = require("engram/database");
var extractTitle = require("engram/extract-title");
var requestURL = require("engram/request-url");

var updateTitle = function (url, title, callback) {

	var sql = "UPDATE url SET title=$title WHERE url.url = $url";

	database.serialise(function (db) {

		db.run(sql, {
			$url: url,
			$title: title
		}, function (err) {
			callback(err ? Err(err) : Ok(url));
		});
	});
};

var saveTitle = function (_ref, reporter) {
	var url = _ref.url;
	var res = _ref.res;
	var body = _ref.body;

	extractTitle({ url: url, res: res, body: body }, function (titleResult) {

		titleResult.onErr(function (err) {
			reporter(Err(err));
		});

		titleResult.onOk(function (title) {
			updateTitle(url, title, reporter);
		});
	});
};

module.exports = saveTitle;