
"use strict"


var fs     = require('fs')





var checkTLSCredentials = (args, callback) => {

	try {

		var keypathExists  = fs.lstatSync(args.keypath) .isFile( )
		var certpathExists = fs.lstatSync(args.certpath).isFile( )

		callback(keypathExists && certpathExists)

	} catch (err) {
		callback(false)
	}

}




module.exports = checkTLSCredentials
