
"use strict"




var fs     = require('fs')
var is     = require('is')





var writeTLSCredentials = (keypath, certpath, keys) => {

	writeTLSCredentials.precond(keypath, certpath, keys)

	try {

		fs.writeFileSync(keypath,  keys.serviceKey)
		fs.writeFileSync(certpath, keys.certificate)

		return null

	} catch (err) {

		return err

	}

}

writeTLSCredentials.precond = (keypath, certpath, keys) => {

	is.always.string(keypath)
	is.always.string(certpath)
	is.always.object(keys)

}





module.exports = writeTLSCredentials
