#!/usr/bin/env node




var http      = require('http')
var should    = require('should')
var request   = require('supertest')
var engram    = require('engram/engram')










var app

describe('/:path', ( ) => {

	before(done => {

		app = engram({
			port:     5000,
			dbpath:  ':memory:',
			start:   true
		})

		setTimeout(done, 1000)

	})





	;['/ftp://foo.bar', '/file://baz.bar']
	.forEach(url => {

		it('should 404 for non-http/https URLs', done => {

			request(app)
			.get(url)
			.expect(404, done)

		})

	})



	;[
		'/http://google.com',
		'/https://google.com',
		'/http://google.com?foo=bar&bash=baz',
		'/https://bar/baz/?foo=bar&bash=baz',
		'/www.google.com?foo=bar&bash=baz'
	]
	.forEach(url => {

		it('should 204 for http and https', done => {

			request(app)
			.get(url)
			.expect(204, done)

		})

	})

	it('should be able to recover bookmarks after saving', done => {

		request(app)
		.get('/example.com')

		request(app)
		.get('/bookmarks/1')
		.expect('Content-Type', 'application/json; charset=utf-8')
		.expect(200, done)

	})

})
