#!/usr/bin/env node




var http      = require('http')
var should    = require('should')
var request   = require('supertest')
var engram    = require('engram/engram')




var app

describe('/*', ( ) => {

	before(done => {

		app = engram({
			port:     5000,
			dbpath:  ':memory:'
		})

		setTimeout(done, 2040)

	})

	it('should 404 for non-http/https URLs', ( ) => {

		request(app)
		.get('/ftp://foo.bar')
		.expect(404)
		.end(err => {
			if (err) throw err
		})

		request(app)
		.get('/file://baz.bar')
		.expect(404)
		.end(err => {
			if (err) throw err
		})

	})


	it('should 204 for http', ( ) => {

		request(app)
		.get('/http://google.com')
		.expect(204)
		.end(err => {
			if (err) throw err
		})

	})


	it('should 204 for https', ( ) => {

		request(app)
		.get('/https://google.com')
		.expect(204)
		.end(err => {
			if (err) throw err
		})

	})


	it('should 204 with query string', ( ) => {

		request(app)
		.get('/http://google.com?foo=bar&bash=baz')
		.expect(204)
		.end(err => {
			if (err) throw err
		})

		request(app)
		.get('/https://bar/baz/?foo=bar&bash=baz')
		.expect(204)
		.end(err => {
			if (err) throw err
		})

	})

	it('should 204 without the protocol',( ) => {

		request(app)
		.get('/www.google.com?foo=bar&bash=baz')
		.expect(204)
		.end(err => {
			if (err) throw err
		})

	})

})
