#!/usr/bin/env node





var should    = require('should')
var request   = require('supertest')
var engram    = require('engram/engram')





describe('/*', ( ) => {

	var app = engram(5000)

	it('should 404 for non-http URLs', ( ) => {

		setTimeout(( ) => {

			request(app)
			.get('/ftp://foo.bar')
			.expect(404)
			.end((err, res) => {

				if (err) {
					throw err
				}

			})


		})


	})

})
