#!/usr/bin/env node
"use strict";

var should = require("should");
var request = require("supertest");
var engram = require("engram/engram");

describe("/*", function () {

	var app = engram(5000);

	it("should 404 for non-http URLs", function () {

		setTimeout(function () {

			request(app).get("/ftp://foo.bar").expect(404).end(function (err, res) {

				if (err) {
					throw err;
				}
			});
		});
	});
});